//The snail never climbs a negative distance. If the fatigue factor drops the snail’s climbing distance below zero, the snail does not climb at all that day.#include<bits/stdc++.h>using namespace std;int main(){    double height,wall_height,accelaretion,slide,fatigue;    while(scanf("%lf%lf%lf%lf",&wall_height,&accelaretion,&slide,&fatigue)==4&&(wall_height!=0))    {        int f=0,day=0;        fatigue*=(accelaretion/100);        height=0;        for(int day=1;;day++)        {            height+=accelaretion;            if(height>wall_height)            {                printf("success on day %d\n",day);                break;            }            if(height<0)            {                printf("failure on day %d\n",day);                break;            }            height-=slide;            if(height<0)            {                printf("failure on day %d\n",day);                break;            }            accelaretion-=fatigue;            if(accelaretion<0)accelaretion=0;        }    }    return 0;}