#include<bits/stdc++.h>using namespace std;int main(){    int t;    while(cin>>t)    {        while(t--)        {            vector <int> v;            int n;            cin>>n;            for(int i=0; i<n; i++)            {                int a;                cin>>a;                v.push_back(a);            }            sort(v.begin(),v.end());            int sum=0,mid=0;            if(n%2==0)mid=(v[n/2]+v[n/2-1])/2;            else mid=v[n/2];            for(int i=0; i!=v.size(); i++)            {                int b=mid-v[i];                if(b<0)b*=(-1);                sum+=b;            }            cout<<sum<<endl;        }    }    return 0;}